mapper.update:


UPDATE rboard
set hit = hit+1
where no = #{int}



UPDATE rboard
set order_no = order_no+1
where group_no = #{groupNo} 
and order_no > #{orderNo}



UPDATE rboard
set title = #{title}
    ,content = #{content}
where user_no = #{userNo}
and no = #{no}

mapper.delete:

UPDATE rboard
set title = '삭제된 게시물 입니다.'
    ,content = '내용없음'

where user_no = #{userNo}
and no = #{no}


DELETE from rboard
where user_no = #{userNo}
and no = #{no}
